variables.input:
timeseries {
  availability_series = avg(dt.synthetic.browser.availability)
}, by:{dt.synthetic.monitored_entity_ids, dt.entity.synthetic_location, dt.entity.synthetic_test}
| fieldsAdd location = entityAttr(dt.entity.synthetic_location, "entity.name")

| summarize { location_name = takeFirst(location), 
              availability =  avg(arrayAvg(availability_series))
            }, by: {dt.entity.synthetic_location}
| sort availability asc
| fieldsRename  `Location` = location_name, 
                `Availability` = availability
| fieldsRemove dt.entity.synthetic_location
| filter in(`Location`, "Milan")
| fieldsRemove Location

