    appsList = appsList+",\""+$AppName[i]+"\"";
    i++;
  }
  // Calculate Chart Interval
  const timeDiff = (endTime-startTime)/3600/1000/24;
  var chartInterval = "5m"
  if (timeDiff>.5)
    var chartInterval = "10m"
  if (timeDiff>1)
    var chartInterval = "1h"
  const query = 'SELECT DATETIME(endTime,  "MM-dd HH:mm:ss","'+chartInterval+'") AS "Date", CONDITION(count(*), where userExperienceScore="SATISFIED")  AS "Satisfied", CONDITION(count(*), where userExperienceScore="TOLERATED")  AS "Tolerated", CONDITION(count(*), where userExperienceScore="FRUSTRATED")  AS "Frustrated" from usersession WHERE useraction.application in ('+appsList+') and  usersession.starttime>='+startTime+' and usersession.endtime<='+endTime+' GROUP BY  DATETIME(endTime,  "MM-dd HH:mm:ss","'+chartInterval+'") limit 5000';
