    types: [
      {
        indexRange: [0, sampleCount],
        mappings: {
         Date: { type: 'timestamp' },
          'Satisfied': { type: 'double' },
          'Tolerated': { type: 'double' },
          'Frustrated': { type: 'double' }
        },
      },
    ],
  };
}

